void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy - .5;
    uv.x *= iResolution.x/iResolution.y;
    vec3 col = vec3(.5,.5,.5);
    float randyformsouthpark;
    uv *= 3.;
    uv.y += .5;
    vec2 z = uv.yx;
    for(int i =0;i<int(mod(iTime * 10.,100.));i++){
    z = vec2(z.x * z.x - z.y * z.y,2. * z.x * z.y) - uv.yx;

    for(float i = -2.0; i < 2.0;i+= 0.1){
        for(int j = 0; j < 2;j++){
            randyformsouthpark += 1.0 - smoothstep(0.0,0.004,abs(z[j]- i));
        }
    }
    }

    col.g = randyformsouthpark/(1.);
    fragColor = vec4(col,1.0);
}
