const int iterations = 15;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 1.5 * (2.0 * fragCoord - iResolution.xy)/iResolution.y + vec2(3.0,0.0);
    float modVal = 0.1*iTime;
    float val = uv.x;
    for(int i; i < iterations; i++)
    {
        val = mod(pow(val,uv.y),modVal);
    }
    vec3 col = vec3(val/modVal);
    fragColor = vec4(col,1.0);
}
