vec2 hash22(vec2 p){
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);

}

vec2 randDir(vec2 p){
    while (true){
        vec2 rnd = hash22(p)*2.0-1.0;
        if (length(rnd)>0.0001){
            return (rnd);
        }
        p+=vec2(0.0,0.1);
    }
}

vec3 noise(vec2 p){
    vec4 corners = vec4(floor(p),ceil(p));

    vec2 ll = randDir(corners.xy);
    vec2 lh = randDir(corners.xw);
    vec2 hl = randDir(corners.zy);
    vec2 hh = randDir(corners.zw);

    vec2 loc = mod(p,vec2(1.0)); // local coords

    float llDot = dot(ll,(p-corners.xy));
    float lhDot = dot(lh,(p-corners.xw));
    float hlDot = dot(hl,(p-corners.zy));
    float hhDot = dot(hh,(p-corners.zw));

    vec2 easeFac = ((6.0*loc - 15.0)*loc+10.0)*loc*loc*loc; // eased factor
    vec2 easeDif = 30.0*loc*loc*(loc*(loc-2.0)+1.0); // differential of eased factor

    return vec3(
        mix(
            mix(llDot,hlDot,easeFac.x),
            mix(lhDot,hhDot,easeFac.x),
            easeFac.y
        ), // factor

        ll + easeFac.x*(hl-ll) + easeFac.y*(lh-ll) + easeFac.x*easeFac.y*(ll-hl-lh+hh) +
        easeDif*(easeFac.yx*(llDot-hlDot-lhDot+hhDot) + vec2(hlDot,lhDot) - llDot) // Differential
    );
}

vec3 fractal(vec2 p,int iters){
    vec3 val = vec3(0.0);
    vec2 coords = p;
    float total = 0.0;
    for (int i=0;i<iters;i++){
        float magnitude = 1.0/(float(i)+1.0);
        val+=(noise(coords)*vec3(0.5,1.0,1.0)+vec3(0.5,0.0,0.0))*magnitude;
        total+=magnitude;
        coords*=2.0;
    }
    return val/total;
}

vec3 light(vec2 fragCoord,vec2 norm,vec2 pos,vec3 col){
    vec2 diff = fragCoord - pos;
    float intensity = dot(norm,normalize(diff))*0.5+0.5;

    intensity *= iResolution.y/length(diff);

    return col*intensity;
}

// HSV to RGB approximation - not accurate, but im lazy and its good enough
vec3 hsv(vec3 from){
    return mix(max(vec3(cos(from.x),cos(from.x-2.09439510239),cos(from.x-4.18879020479)),vec3(0.0)),vec3(1.0),1.0-from.y)*from.z;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord){
    vec2 uv = fragCoord/150.0+vec2(iTime*0.5,0.0);

    vec3 noise = fractal(uv,12);

    vec3 tex = texture(iChannel0,uv*0.25).rgb;
    vec2 diff = fragCoord-iMouse.xy;
    float dist = length(diff);

    vec3 col = vec3(0.0);

    col += tex*light(fragCoord,noise.yz,(length(iMouse.xy)<0.5 ? iResolution.xy*0.8 : iMouse.xy),hsv(vec3(mod(iTime,6.28318530718),1.0,0.7)));
    col += tex*light(fragCoord,noise.yz,(vec2(sin(iTime),cos(3.0*iTime))+1.5)/3.0*iResolution.xy,vec3(0.2));

    vec3 over = max(col-vec3(1.0),vec3(0.0));
    col += vec3(0.5*length(over));

    // Output to screen
    fragColor = vec4(col,1.0);
}
