void mainImage( out vec4 o, vec2 U )
{
    vec2 R = iResolution.xy,
         u = fract((U+U-R)/R.y), // u becomes grid
         f = floor(u+u);         // f used to rotate mini-cells (id)

    // Split cell into 4 mini-cells and rotate around centre
    // of original cell
    u = mix(u = (u+u-f-.5) * sign(f+--f), 
            u.yx, 
            mod(f.x+f.y,2.));

    // Draw quarter pattern in each mini-cell
    o = vec4(abs(u.x)-.5 > u.y || u.y > 0. && u.x+u.y < .5);
}
