const float ZOOM = 2.0;




void drawPlayer(vec2 playerPos, float playerRot, vec2 worldPos, inout vec4 fragColor) {

    float outer = sdRoundedBox(rot(playerRot) * (worldPos - playerPos), vec2(15.0), vec4(2));

    if (abs(outer - (-16.0 / 4.0)) <= 10.0 / 4.0) {
        fragColor = vec4(0, 1, 0, 1);
        return;
    }
    if (abs(outer - (-16.0 / 4.0)) <= 16.0 / 4.0) {
        fragColor = vec4(0, 0, 0, 1);
        return;
    }

    if (abs(outer - (-60.0 / 4.0)) <= 10.0 / 4.0) {
        fragColor = vec4(0, 1, 1, 1);
        return;
    }
    if (abs(outer - (-60.0 / 4.0)) <= 16.0 / 4.0) {
        fragColor = vec4(0, 0, 0, 1);
        return;
    }
}



void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    State state = read(iChannel0, iResolution.xy);

    float effZoom = ZOOM * iResolution.x / 1200.0;

    vec2 worldPos = (fragCoord - iResolution.xy / 2.0) / effZoom + state.cameraPos;

    fragColor = vec4(0.1, 0.2, 0.4, 1);

    if (worldPos.y < 0.0) {
        if (worldPos.y > -2.0) {
            fragColor = vec4(1);
            return;
        }
        fragColor = vec4(0, 0, 0, 1);
        return;
    }


    ivec2 sections = getMinMaxSection(worldPos.x, 1);

    for (int s = sections.x; s <= sections.y; s++) {
        uvec2 section = SECTIONS[s];
        for (uint i = section.x; i < section.y; i++) {
            drawObject(LEVEL[i], worldPos, fragColor);
        }
    }


    drawPlayer(state.playerPos, state.playerRot, worldPos, fragColor);




    //if (mod(worldPos.x, 30.0) < 1.0 || mod(worldPos.y, 30.0) < 1.0) {
        //fragColor = mix(fragColor, vec4(0, 0, 0, 1), 0.2);
        //return;
    //}

}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    if (fragCoord.y > 2.0) {
        return;
    }

    State state;

    if (iFrame == 0) {
        state = new();
    } else {
        state = read(iChannel0, iResolution.xy);
    }

    update(state, iTimeDelta, iMouse);

    write(state, fragCoord, fragColor);
}
