#define PI 3.1415926535897932384626433832795
#define f(x) pow(abs(cos(x)), 16.)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;
    uv *= 69.;
    float t = iTime / 4.;
    float v =
        cos(uv.x)        * f(t               ) +
        cos(uv.x - uv.y) * f(t +      PI / 4.) +
        sin(uv.y)        * f(t + 2. * PI / 4.) +
        sin(uv.y + uv.x) * f(t + 3. * PI / 4.);
    vec3 c =
        vec3(.1, .3, .4) * pow(v, .420) +
        smoothstep(.8, 1., v) * vec3(1.);
    fragColor = vec4(c, 1.);
}
