renderpass.code:
float r = .2, W;

float s(vec2 p, vec2 b) {
    vec2 d = abs(p) - b;
    return length(max(d, 0.)) + min(max(d.x, d.y), 0.);
}

float p(float t) { return abs(1. - mod(t, 2.)); }
float y(float t) { return p(.4 * t + .1); }
float j(float t) { return 2. * (min(1., 2. * mod(t * .5, 1.)) + floor(.5 * t)) + .1 * sin(4. * t); }

float S(vec2 P) {
    float t = iTime;

    float d = s(P - vec2(-W / 2., 
        min(1. - r, max(0., y(j(t) - 1.))) + r / 2.
    - .5), vec2(.01, r / 2.));
    d = min(d, s(P - vec2(W / 2., 
        min(1. - r, max(0., y(j(t - 1.))) + r / 2.)
    - .5), vec2(.01, r / 2.)));
    d = min(d, s(P - vec2((p(t) - .5) * W, y(t) - .5), vec2(.01)));

    return d;
}

void mainImage(out vec4 c, vec2 U) {
    vec2 r = iResolution.xy;
    W = r.x / r.y * .9;
    U = (U - r / 2.) / r.y;

    c += smoothstep(.01, .0, S(U));
}
