// signed distance to a regular octogon
// Copyright Â© 2018 Inigo Quilez
float sdOctogon( in vec2 p, in float r )
{
  // pi/8: cos, sin, tan.
  const vec3 k = vec3(-0.9238795325,   // sqrt(2+sqrt(2))/2 
                       0.3826834323,   // sqrt(2-sqrt(2))/2
                       0.4142135623 ); // sqrt(2)-1 

  // reflections
  p = abs(p);
  p -= 2.0*min(dot(vec2( k.x,k.y),p),0.0)*vec2( k.x,k.y);
  p -= 2.0*min(dot(vec2(-k.x,k.y),p),0.0)*vec2(-k.x,k.y);
  // Polygon side.
  p -= vec2(clamp(p.x, -k.z*r, k.z*r), r);
  return length(p)*sign(p.y);
}

//Pallete gradient function
vec3 palette( in float t, vec3 a, vec3 b, vec3 c, vec3 d)
{
    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Setup centred screen space
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv; //Keep origional UV
    vec3 col = vec3(1.0);

    //Build color palette
    //col = palette(iTime*5.0, vec3(0.948, 0.500, 0.448), vec3(0.779, 0.242, 0.610), vec3(0.948, 1.058, 1.168), vec3(0.000, 0.333, 0.528)); //Rainbow
    col = palette((fragCoord.x/iResolution.x*0.2) + iTime*0.1, vec3(0.948, 0.500, 0.448), vec3(0.779, 0.242, 0.610), vec3(0.948, 1.058, 1.168), vec3(0.000, 0.333, 0.528));


    //Make the octogon
    float oct = 0.0;
    bool inner = false;

    oct = sdOctogon( uv, 18.0 + sin(iTime*1.2)/20.0);
    oct = sin(oct*6.0)/15.0;

    oct = abs(oct);
    oct = 0.03 / oct;

    col -= 0.1 + sin(iTime*80.0)/8.0;
    col *= oct;


	fragColor = vec4(col,1.0);
}
