#define R2D(a) mat2( cos( a + vec4(0,11,33,0 )))

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e,t=iTime; vec3 p,q=vec3(-3,0,3),r=iResolution;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*r.xy)/r.y,e-.2)*4.;
    p.z-=6.;
    p.xz*=R2D(t*.5);

    for(j=0.;j++<8.;)
        p=abs(p*e-.05)-.046,
        d=max(max(p.z,p.y),p.x),
        d-=dot(p,q)/1e4;

    e-=d*log(d);
}o+=vec4(q/p*e,1)/i;
}
