void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 l = normalize(vec3(.5,1.,2.));
    vec3 a = normalize(vec3(-texture(iChannel0, (fragCoord) / iResolution.xy+vec2(.00001,0.)).x+texture(iChannel0, (fragCoord) / iResolution.xy).x,
                    -texture(iChannel0, (fragCoord) / iResolution.xy+vec2(0.,.00001)).x+texture(iChannel0, (fragCoord) / iResolution.xy).x,.000001));
    float lt = pow(dot(a,l) * 0.4 + 0.6,8.) + smoothstep(.7, .8, pow(dot(a,l) * 0.4 + 0.6,60.));
    if(texture(iChannel0, (fragCoord) / iResolution.xy).x > 0.001) {
        fragColor = vec4(0.3*lt,0.8*lt,1.5*lt*(.5+.5*sin(3.*iTime)),0.);
    } else {
        fragColor = vec4(0.3,0.6,0.6,0.);
    }
}

float smoothMax(float a, float b, float k) {
    k = 2.*k;
    return k*log(exp(a/k)+exp(b/k));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 Mouse = vec2 (0., 0.);
    if (iMouse.z > 0.) {
        Mouse = iMouse.xy;
    } else {
        Mouse = vec2(.5 + .3 * sin(2.03*iTime+(1.+1.*sin(1.1*iTime))), .5 + .3*cos(3.05*iTime+3.7)) * iResolution.xy;
    }
    float dMouse = length(Mouse - fragCoord) / iResolution.x;
    dMouse = .005 - dMouse * dMouse;
    float d = 0.;
    float d0 = texture(iChannel0, fragCoord / iResolution.xy).x;
    d = 0.99*smoothMax(d0, dMouse, 0.001) - .00004;
    fragColor = vec4(max(0.,d),0.,0.,0.);
}
