#define pi 3.1415926

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
float tim = iTime*0.1;
    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    float x = sin(uv.x+tim*.11553513)+sin(uv.y+tim*.12321451) + (uv.x+uv.y+tim+3.);
    float b = atan(mod(x*15.,1.));
    float w=pi*b*2.;
    b = (sin(w)+w)/(2.*pi);
    col = vec3(b);
    // Output to screen
    fragColor = vec4(col*.3+0.7,1.0);
}
