vec3 stereographic_proj(float X, float Y) //https://en.wikipedia.org/wiki/Stereographic_projection#First_formulation 
{
    float div = (1.0+X*X+Y*Y);
    return vec3(2.0*X / div, 2.0*Y / div, (-1.0+X*X+Y*Y) / div);
}

vec3 checkerboard(vec2 p)
{
    float ncx = 12.0; //number of cells x
    float ncy = 12.0;
    return vec3(mod(mod(floor(p.x*ncx +iTime), 2.0) + mod(floor(p.y*ncy+iTime), 2.0),2.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = checkerboard(
        stereographic_proj((uv.x-0.5)*10.0, (uv.y-0.5)*10.0).xy
    );

    fragColor = vec4(col, 1.0);
}
