int place(int a)
{
    if (a > 0) return 1;
    if (a <= 0) return -1;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (((fragCoord/iResolution.xy) * 2.) - 1.) * 20.;

    uv.x /= 1.5;

    float y = cos(abs(uv.x - iTime * 5. * float(place(int(ceil(uv.x)))))) * uv.x;

    fragColor = vec4(vec3(0.),1.0);

    if (uv.y < y + .1 * abs(uv.x) && uv.y > y - .1 * abs(uv.x))
    {
        fragColor = vec4(vec3(1.),1.0);
    }
}
