// the following code is altered from https://www.shadertoy.com/view/4tfSz8 

#define iMax 1024.
#define COL_NUM 256.

float random (in vec2 _st) {
    return fract(sin(dot(_st.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
}

vec3 julia(vec2 z)
{
	float j = 0.0;
	for(float i = 0.0; i<iMax; i++)
	{
		z = vec2(z.x*z.x-z.y*z.y, z.x*z.y+z.x*z.y);
        z = vec2(z.x*z.x-z.y*z.y, z.x*z.y+z.x*z.y)+vec2(smoothstep(20.0, 40.0, mod(random(z)*50., 50.)))*5.;
		j = i;
		if(length(z) > 2.0) break;
	}
	return vec3(j/COL_NUM/1.,j/COL_NUM/1.,j/COL_NUM/1.);

}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv = fragCoord/iResolution.xy*2.0-1.0;
	uv.x*=1.78;
	uv*=0.3*sin(iTime*0.2)+1.5;
	float ang = iTime*0.1;
	mat2 rot = mat2(cos(ang),-sin(ang),sin(ang),cos(ang)); 
	uv*=rot;

    vec3 col = julia(uv);

	fragColor = vec4(col,1.0);
}
