#define DURATION 5.
#define TRESHOLD .2
#define PI 3.14159

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float t = sin((mod(iTime , DURATION*2.) / DURATION)*PI)*.5+.5;

    vec4 tex0 = texture(iChannel0,uv);
    vec4 tex1 = texture(iChannel1,uv);

    float s = 0.;
    if (uv.x > t){
        s = 0.;
    }else{
        s = (t-uv.x)*9.;
    }
    if (s > 1.)
        s = 1.;
    if (s < 0.)
        s = 0.;

    // Output to screen
    fragColor = tex0*s + tex1*(1.-s);
}
