//inspiration from nbardy : https://www.shadertoy.com/view/tslcW4
const float a = 0.9,
            b = 0.1759,
            PI = 3.14159265359,
            TWO_PI = 2.0 * PI,
            PALE_NUMBER = 4.0,
            SPEED = 8.0,
            THICKNESS = 1.0,
            SIZE = 5.0,
            FX = 1.0,
            FY = 1.0;



float spiralSDF(vec2 p) {
    float angle = atan(p.y * FX, p.x * FY);
    float t = PALE_NUMBER * angle + iTime * SPEED;
    float r = length(p) * THICKNESS;
    float n = (log(r * SIZE / a) / b - t) / TWO_PI;
    vec2 d = abs(a * exp(b * (t + TWO_PI * floor(n) + vec2(0, 1))) - r);

    return d.x;
}

void mainImage(out vec4 O, vec2 I) {
    vec2 R = iResolution.xy;
    vec2 uv = (2.0 * I - R) / R.y;
    float d = spiralSDF(uv);
    float v = pow(d, 1.32) / 0.93;

    O.rgb = vec3(v);
}
