float Circle(vec2 uv , vec2 point , float r)
{

    float distPoint = length(uv - point);


    float smoothness = smoothstep(r, r-0.01 , distPoint);


    return smoothness;



}




void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  // Get mouse position data
  vec2 uv = fragCoord.xy / iResolution.xy;

  vec4 m = iMouse / iResolution.x;

  uv -= 0.5;
  uv *= 3.;
  float mouseX = m.x;
  float mouseY = m.y;

  float col = 0.;





  for(float i = 0.0; i < 40.; i++)
	{
	  float si = sin(iTime + i * mouseY * 0.5 );
	  float co = cos(iTime + i * mouseX * 0.5  );

	  col += Circle(uv , vec2(si , co),0.1);
	}




  fragColor = vec4(vec3(col), 1.0);
}
