// Technique from a @The_ArtOfCode tutorial. Go follow him ðŸ™
//    => https://www.youtube.com/watch?v=xDxAnguEOn8
//
// Instead of flat-space we put the fireworks on a spherical surface
// in order to create a crazy equirectangular fireworks skybox texture. 
//
// Some things I'd like to add (but ran out of time): 
//    => Performance upgrades 
//    => Experiment w/raymarching a firework
//    => Account for distance-to-camera in particle size
// ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~


// Create random point on normal-sphere from float
vec3 Hash123(float t) {
  float x = fract(sin(t * 456.51) * 195.23);
  float y = fract(sin((t + x) * 951.2) * 462.1);
  float z = fract(sin((t + x + y) * 375.2) * 108.1);
  return normalize((vec3(x, y, z)-.5)*2.);//.;
}


// This function let's us jump from 2D-UV to spherical 3D-XYZ position
vec3 uvTo3D(vec2 uv) {
    // The 2-Sphere point via UV => Lat/Long angles
    float theta = uv.x * 2.0 * 3.14159265359; // Longitude
    float phi = uv.y * 3.14159265359; // Latitude
    // Trigonometry from 2-Sphere angles to an XYZ normalized [-1,1].
    float x = sin(phi) * cos(theta);
    float y = sin(phi) * sin(theta);
    float z = cos(phi);
    // { Dev Note }
    // If you're porting this shader to a material, I strongly recommend you skip this function and just use the XYZ of your `varying vNormal` in place of the result you would get here. Should be suitable for all spheres and most round geometries
    return vec3(x, y, z);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col= vec3(0.);
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec3 pos = uvTo3D(uv);
    pos = normalize(pos);
    //pos+=1.;
    //float t = fract(iTime);
    float sTime=iTime*.5;
    vec3 color = sin(4.0 * vec3(0.34, 0.54, 0.43) * floor(sTime)) * 0.25 + 0.75;

    for(float j=0.;j<4.;j++){
        float t=fract(sTime+(.25*j));
        vec3 dir = Hash123(floor(sTime+(.25*j))+j);

        for(float i=0.;i<50.;i++){
            vec3 dir2 = Hash123(floor(sTime)+i)*1.5;
            float d = length(pos-normalize(dir+dir2*t));
            //d=length(pos);
            //float brightness=.001;
            float brightness = mix(0.0005, 0.001,smoothstep(.5,0.,t));
            brightness*=sin(t*20.+i)*.25+.75;
            brightness*=smoothstep(1.,.05,t);
            //if(brightness<.001){
            //    brightness*=smoothstep(1.,.05,length(pos-dir)*1.);
            //}
            col+=brightness/d;
            //col*=2.;
            //col*=;        
            }
        col*=color;
        col*=2.;
        color = sin(4.0 * vec3(0.34, 0.54, 0.43) * floor(sTime+j)) * 0.25 + 0.75;
    }
    fragColor.xyz=col;

}
