float sdSquare(in vec2 p, in float a){
    p = abs(p) - a;
    return max(p.x, p.y);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;

    // Parameters for the circle
    vec3 col = vec3(0.380, 0.380, 0.380);
    vec3 baseColor = vec3(0.439, 0.439, 0.439);
   vec3 finalColor = vec3(0.0);
    // Calculate distance from center (d) and apply some function
    float d = length(uv);  
    d = sin(d * 9.0 + iTime) / 9.0;  
    d = abs(d);
    float radius = d;
    d = step(0.02, d); 
    finalColor+= (col + d)*d;
    // Define points on the circle
    float angleIncrement = radians(90.0);  
    vec2 polarPoints[3];
    for (int i = 0; i < 3; i++) {
        float angleRadians = float(i) * angleIncrement;
        polarPoints[i] = vec2(angleRadians, radius);
    }

    // Connect points with lines
    float lineWidth = 0.01; 
    vec3 lineColor = vec3(1.0, 0.0, 0.0);  
    for (int i = 0; i < 3; i++) {
        vec2 pointA = polarPoints[i];
        vec2 pointB = polarPoints[(i + 1) % 3];

        // Calculate direction and distance between points
        vec2 dir = pointB - pointA;
        float dist = length(dir);

        // Calculate distance from current pixel to the line between pointA and pointB
        float lineDist = abs(dir.y * uv.x - dir.x * uv.y + pointB.x * pointA.y - pointB.y * pointA.x) / dist;

        // Draw line
        float lineFactor = smoothstep(lineWidth, lineWidth + 0.001, lineDist);
        col += lineColor * lineFactor;
    }

    // Output color
    col /= finalColor;
    fragColor = vec4(col, 1.0);
}
