/////////
//flames
/////////
#define DECAY 0.95
#define PIXEL_SIZE 80.0

//hash22 from: https://www.shadertoy.com/view/ldc3RB
float hash21(vec2 p)
{
    p = vec2( dot(p,vec2(127.1,311.7)),
			  dot(p,vec2(269.5,183.3)));
    return -1.0 + 2.0 * fract(sin(p)*43758.5453123).x;
}
/////////

float rand21(vec2 p, float range)
{
    return floor( range * hash21(p * mod(iTime, 255.0)) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    uv *= PIXEL_SIZE;
    vec2 frac = floor(uv);

    float temp = (frac.y < 1.0)? 1.0 : 0.0;
    if(frac.y > 0.0)
    {
        temp = texture(iChannel0, (uv - vec2(rand21(frac, 1.0) + 0.5, 1.0))/ PIXEL_SIZE).r * (DECAY +  rand21(frac, 2.0) /20.0 );
    }

    fragColor = vec4(vec3(temp),1.0);
}

