void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float temp = texture(iChannel0, uv).x;
    vec3 col = mix(vec3(0.9, 0.2, 0.0), vec3(5.0, 1.0, 0.0), temp);

    if(temp < 0.05) col = vec3(0.0);

    fragColor = vec4(col, 1.0);
}
