//This vvv was found here > https://www.shadertoy.com/view/XtGGzG
vec3 inferno_quintic( float x )
{
	x = clamp(x, 0.0, 1.0);
	vec4 x1 = vec4( 1.0, x, x * x, x * x * x ); // 1 x x2 x3
	vec4 x2 = x1 * x1.w * x; // x4 x5 x6 x7
	return vec3(
		dot( x1.xyzw, vec4( -0.027780558, +1.228188385, +0.278906882, +3.892783760 ) ) + dot( x2.xy, vec2( -8.490712758, +4.069046086 ) ),
		dot( x1.xyzw, vec4( +0.014065206, +0.015360518, +1.605395918, -4.821108251 ) ) + dot( x2.xy, vec2( +8.389314011, -4.193858954 ) ),
		dot( x1.xyzw, vec4( -0.019628385, +3.122510347, -5.893222355, +2.798380308 ) ) + dot( x2.xy, vec2( -3.608884658, +4.324996022 ) ) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    //Get The Worm
    vec3 col = texture(iChannel0, uv).rgb;
    //Make The Worm 0-1 (I Think)
    col = tanh(col * 0.5);
    //Colour The Worm
    col = inferno_quintic(col.r);

    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;
    vec2 uv2 = fragCoord.xy / iResolution.xy;

    //Create The Circle For The Worm
    float v = step(length(uv + vec2(fract(iTime)*2.0-1.0, sin(iTime * 10.0) * 0.1)), 0.1);

    //Make The Worm Long And Slightly Blur It.
    v += texture(iChannel3, uv2, 2.0).r;
    v /= 1.05;

    fragColor = vec4(v);
}
