float scale(vec3 p){
return distance(vec3(0,0,0),p);
}

float getDist(vec3 origin){
return 
min(
distance(origin,vec3(0,5.0*cos(iTime),10.0))-.5
,

distance(origin,vec3(3.0*sin(iTime/3.0),3.0,10.0))-2.0
)
;
//    return 1.0 - scale(origin);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float viewdist = 50.0;
    float maxscan = 100.0;
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 cv = (uv - vec2(.5,.5)) * 2.0;
    vec3 proj = vec3(-cv.x,-cv.y,1);
    vec3 projnorm = proj/scale(proj);
    vec3 col = projnorm; //something by default

    vec3 testSend=vec3(0,0,0);

    float steps = 0.0;
    float mindist = maxscan;
    float dist = getDist(testSend);
    bool done = false;
    while (scale(testSend)<=maxscan && mindist > 0.0 && steps < 10.0 && !done){

        testSend = testSend + (projnorm * dist);
        dist = getDist(testSend);
        if (dist < mindist) mindist = dist;
        steps+=1.0;
        if (mindist < 0.0) done = true;
    }
    float polarity = 0.0;
    if(mindist > 0.0) polarity = 1.0;
    float scroller = mod(mindist+iTime,1.0);
    col = vec3(
    //scale(testSend)/maxscan*.5,
    //steps/10.0
    //0.0,0.0
    scroller * polarity
    ,0.0,
    scroller * (1.0 - polarity)
    );
    // Output to screen


   fragColor = vec4(col,1.0);
}
