void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor.r = texelFetch(iChannel0, ivec2(fragCoord+cyanOffset*float(1<<dotSize)), 0).r;
    fragColor.g = texelFetch(iChannel0, ivec2(fragCoord+magentaOffset*float(1<<dotSize)), 0).g;
    fragColor.b = texelFetch(iChannel0, ivec2(fragCoord+yellowOffset*float(1<<dotSize)), 0).b;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float c = cos(angle);
    float s = sin(angle);
    mat2 rotMat = mat2(c,-s,s,c);
    float mult = (1.42*sqrt(dot(iResolution.xy,iResolution.xy)))/min(iResolution.x,iResolution.y);
    fragCoord -= iResolution.xy*0.5;
    fragCoord *= mult;
    fragCoord = hexMat*fragCoord;
    fragCoord = rotMat*fragCoord;
    fragCoord += iResolution.xy*0.5;
    fragColor = texture(iChannel0,fragCoord/iResolution.xy);
}

// You can change these
float angle = 4.; // In radians
int dotSize = 1; // Logarithmic scale (base 2)

vec2 cyanOffset = vec2(0,1); // Color offsets are proportional to dot size
vec2 magentaOffset = vec2(-0.58,-0.5);
vec2 yellowOffset = vec2(0.58,0.5);


// Don't change these unless you know what you are doing
mat2 invHexMat = mat2(1.1547,-0.57735,0.,1.);
mat2 hexMat = mat2(0.866,0.5,0.,1.);
vec2 offsets[7] = vec2[7](
    vec2(-1, 1),
    vec2(-1, 0),
    vec2( 0, 1),
    vec2( 0, 0),
    vec2( 0,-1),
    vec2( 1, 0),
    vec2( 1,-1)
);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float c = cos(angle);
    float s = sin(angle);
    mat2 rotMat = mat2(c,s,-s,c);
    float mult = min(iResolution.x,iResolution.y)/(1.42*sqrt(dot(iResolution.xy,iResolution.xy)));
    vec2 tmpCoord = fragCoord - iResolution.xy*0.5;
    tmpCoord *= mult;
    tmpCoord = rotMat*tmpCoord;
    tmpCoord = invHexMat*tmpCoord;
    tmpCoord += iResolution.xy*0.5;
    tmpCoord = vec2((ivec2(tmpCoord)>>dotSize)<<dotSize);
    mult = 1./mult;
    rotMat = mat2(c,-s,s,c);
    float maxRad = 0.7 * mult * float(1<<dotSize);

    fragColor = vec4(1);
    for(int i = 0; i < 7; i++){
        vec2 pix = tmpCoord + offsets[i]*float(1<<dotSize);
        vec3 color = 1. - texelFetch(iChannel0,ivec2(pix)>>dotSize,dotSize).rgb;
        color = sqrt(color);
        color *= maxRad;
        //color *= color;
        vec2 center = pix + 0.5*float(1<<dotSize);
        center -= iResolution.xy*0.5;
        center *= mult;
        center = hexMat*center;
        center = rotMat*center;
        center += iResolution.xy*0.5;
        vec2 diff = fragCoord - center;
        fragColor.r *= clamp(sqrt(dot(diff,diff))-color.r+1.,0.,1.);
        fragColor.g *= clamp(sqrt(dot(diff,diff))-color.g+1.,0.,1.);
        fragColor.b *= clamp(sqrt(dot(diff,diff))-color.b+1.,0.,1.);
    }
}
