#define T iTime

float noise(vec2 p, float ld) {return textureLod(iChannel0, p, ld).r;}

float lines(vec2 p, float n, float ld) {
    return clamp(exp(tan((noise(p+0.1*n*vec2(cos(0.25*n+iTime),sin(0.25*n+iTime)),ld))
       *6.28*0.5)-16.),0.0,1.0);
}

void mainImage(out vec4 fragColor,in vec2 fragCoord) {
    vec2 uv = (fragCoord.xy-0.5*iResolution.xy)/iResolution.y*0.5;
    vec3 col = vec3(0.0);
    float pat = 0.0;
    float c = 10.0;
    float amp = 1.0;
    float div = 0.0;
    float ld = mix(2.0, 3.0, 0.5+0.5*sin(0.25*iTime+6.28*cos((iTime-3.29816)*0.25)));
    float freq = 0.6;
    for (float i = 0.0; i < c; i++) {
        float ni = i / c;
        float a = ni * 6.28;
        vec2 alt = vec2(sin(a-ni*pat), cos(a+ni*pat));
        pat += amp*lines(uv*freq+alt, noise((uv+alt)*0.5*freq, ld), ld*1.1);
        if (pat > amp*0.5) break;
        div += amp;
        amp *= 0.5;
        freq *= 2.0;
    }
    pat /= div;
    col += pat;
    col = max(col, vec3(0.0));
    col = pow(col, vec3(1.0 / 2.2));
    fragColor = vec4(col,1.0);
}
