void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float zoom = pow(2.0, iTime);
    float startSize =  pow(2., 10.);
    float zoomOffset = 0.5;

    vec2 px = (fragCoord/iResolution.xy - zoomOffset) * startSize;
    px /= zoom; // Should make it zoom out... doesn't anymore because of cycle stuff

    // Cycle stuff
    float cycleProgress = fract(iTime / 2.);
    px = (px *= zoom) * exp2(cycleProgress);

    // Rotate all pixels
    vec2 pxr = px * mat2(.87,-.5,0,1);
    // Binary AND create serpinski
    float r = float(int(pxr.x)&int(pxr.y));
    // Color!
    fragColor = r != 0. ? vec4(0,0,0,0) : vec4(1,1,1,0);
}
