mat2 rot2(float angle) {
    float ca = cos(angle);
    float sa = sin(angle);
    return mat2(ca, sa, -sa, ca);
}

float waves(vec2 uv) {
    uv *= rot2(radians(55.));

    float v = sin(-cos(uv.x*0.8)*28.+uv.y*18. + iTime * 25.);

    return smoothstep(-1., 1., v);
}

float rectangle(vec2 uv) {
    float blur = dFdy(uv.y);
    uv *= rot2(radians(45.0));
    float height = 1. - smoothstep(0.0, blur, abs(uv.y) - 1.5);
    float width = 1. - smoothstep(0.0, blur, abs(uv.x + 5.0) - 3.25);
    return height * width;
}

float border(vec2 uv) {
    float blur = dFdx(uv.x);
    return smoothstep(0.0, blur, max(abs(uv.x), abs(uv.y)) - 10.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy * 0.5) * 20. / min(iResolution.x, iResolution.y);

    float c = waves(uv);

    c = mix(c, 1.0, rectangle(uv));

    c = mix(c, 0.0, border(uv));

    vec3 col = vec3(c);

    fragColor = vec4(col,1.0);
}
