vec3 Phlox = vec3(0.835, 0.176, 0.898);
vec3 bgColorAdarker = vec3(0.216, 0, 1);

vec3 noisetest(vec2 uv){
    vec3 color = vec3(0);

    color = vec3(fract(sin(dot(floor(uv*60.), vec2(15., 78.))) * 1. * iTime ) - 0.8);
    color = vec3(mix(color, bgColorAdarker, smoothstep( 3., -2.5, pow(0.3*uv.x, 2.) + uv.y + .5)));
    color = vec3(mix(color, Phlox, smoothstep( 1.5, -2.5, pow(0.3*uv.x, 2.) + uv.y + .5)));

    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 baseUV = fragCoord/iResolution.xy;
    vec2 uv = baseUV * 2.f - 1.f;
    uv.x *= iResolution.x / iResolution.y;

    // Output to screen
    fragColor = vec4(noisetest(uv),1.0);
}
