float colorDist(vec3 c1, vec3 c2)
{
    return length(abs(c1-c2));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col0 = texture(iChannel0,uv).xyz;
    vec3 col1 = texture(iChannel0,uv - 8./vec2(iResolution.xy)).xyz;
    vec3 col2 = texture(iChannel0,uv + vec2(16./iResolution.x,0.)).xyz;
    vec3 col3 = texture(iChannel0,uv + vec2(0.,16./iResolution.y)).xyz;

    vec3 col = vec3(
        colorDist(col0,col1),
        colorDist(col0,col2),
        colorDist(col0,col3)
    );


    // Output to screen
    fragColor = vec4(col,1.0);
}
