#define t iTime

float srf(vec2 uv){
    float tx = uv.x;
    float ty = uv.y;

    float rx = (tx*tx)-(ty*ty);
    float ry = rx*(ty-tx);

    float fx = abs(rx*ry);
    return fx;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord/iResolution.xy)*2.;

    vec3 col = vec3(0);
    col.rb += fract(srf(uv+t));

    fragColor = vec4(col,1.0);
}
