void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
        vec2 uv = (fragCoord.xy-iResolution.xy*.5)/min(iResolution.x, iResolution.y);

    float n_tau = radians(360.);
    float n_amp = 0.125;
    float n_freq = 2.;
    float n_a = uv.y-sin((uv.x)*n_freq*n_tau+iTime)*n_amp;
    float n_b = uv.y-sin((uv.x)*n_freq*n_tau-iTime)*n_amp;
    float n_c = uv.y - n_a - n_b;
    float na = abs(n_a);
    float nb = abs(n_b);
    float nc = abs(n_c);

    float b = .04;
    float na2 = b *(1./(na+b));
    float nb2 = b *(1./(nb+b));
    float nc2 = b *(1./(nc+b));

    vec3 o_col = vec3(na2, nb2, nc2);
    fragColor = vec4(o_col, 1.);
}
