void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv /= min(iResolution.x, iResolution.y) * 0.0035;

    vec3 rColor = vec3(0.9, 0.0, 0.3);
    vec3 gColor = vec3(0.0, 0.9, 0.3);
    vec3 bColor = vec3(0.0, 0.3, 0.9);
    vec3 yColor = vec3(0.9, 0.9, 0.9);

    // Time varying pixel color
    // vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    float a = sin(uv.y * 3.0 - iTime * 0.2) / 1.0;
    float b = cos(uv.y * 4.0 - iTime * 0.2) / 1.5;
    float c = sin(uv.y * 5.0 - iTime * 0.2 + 3.14) / 2.0;
    float d = cos(uv.y * 6.0 - iTime * 0.2 + 3.14) / 3.0;

    float e = 0.04 / abs(uv.x + a);
    float f = 0.05 / abs(uv.x + b);
    float g = 0.03 / abs(uv.x + c);
    float h = 0.025 / abs(uv.x + d);

    vec3 gradCol = 0.6 + 0.5*cos(iTime+uv.xyx+vec3(2,1,0));

    vec3 destColor = rColor * e + gColor * f + bColor * g + yColor * h;

    float blendFactor = 0.5;
    vec3 finalColor = mix(gradCol, destColor, blendFactor);

    fragColor = vec4(finalColor, 1.0);

}
