void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Convert pixel coordinates to a range of -1 to 1, centered at the screen
    vec2 uv = (fragCoord / iResolution.xy) * 2.0 - 1.0;

    // Adjust UV coordinates back to a range of 0 to 1 for texture sampling
    vec2 uvTex = uv * 0.5 + 0.5;

    // Get the color from the distortion texture, adding time to make it move
    vec4 dist = texture(iChannel1, uvTex + (iTime * 0.02));

    // Use the red channel of the distortion texture to create a small offset
    vec2 distortionOffset = dist.rr * vec2(0.05, 0.05);

    // Apply the offset to the UV coordinates to get a distorted color from the main image texture
    vec4 color = texture(iChannel0, uvTex + distortionOffset);

    // Output the final color of the pixel
    fragColor = color.rgba;
}
