float drawCapsule(in float longSide, in float shortSide, in float differenceXY) {
    if (longSide < 0.) {
        differenceXY = -differenceXY;
    }

    if (abs(longSide) < abs(differenceXY)) {
        // Space between the circle along the longSide.
        // Always use the longest distance possible to cover the space.
        longSide = 0.;
    } else {
        // Increase the circle distance along the longSide by differenceXY.
        longSide -= differenceXY;
    }

    float rawDistanceToCenter = length(abs(vec2(longSide, shortSide)));
    return rawDistanceToCenter;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Move the coordinate zero to the center.
    vec2 centeredCoordinate = fragCoord.xy - iResolution.xy / 2.;

    float capsuleSize = 0.9;

    float differenceXY = (iResolution.x - iResolution.y) / 2.;
    if (differenceXY > 0.) {
        // The resolution is wider than it is tall.
        float distanceToCenter = drawCapsule(centeredCoordinate.x, centeredCoordinate.y, differenceXY);
        float relativeDistanceToCenter = distanceToCenter / (iResolution.y / 2.);
        fragColor = vec4(vec3( step(capsuleSize, relativeDistanceToCenter) ),1.0);
    } else {
        // The resolution is taller than it is wide.
        differenceXY = abs(differenceXY);
        float distanceToCenter = drawCapsule(centeredCoordinate.y, centeredCoordinate.x, differenceXY);
        float relativeDistanceToCenter = distanceToCenter / (iResolution.x / 2.);
        fragColor = vec4(vec3( step(capsuleSize, relativeDistanceToCenter) ),1.0);
    }
}
