void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.xy;

    vec4 texture01 = texture(iChannel0, uv);
    texture01.r += 0.04;
    texture01.g += 0.04;
    texture01.b -= 0.04;
    texture01 = vec4(dot(texture01, vec4(0.393, 0.769, 0.189, 0)),
                     dot(texture01, vec4(0.349, 0.686, 0.168, 0)),
                     dot(texture01, vec4(0.272, 0.534, 0.131, 0)),
                         texture01.a);
    texture01 = clamp(texture01, 0.1, 1.0);

    vec4 colorsPhoto = vec4(texture01.r + sin(0.5 * iTime) * (uv.x * 1.5), texture01.g * 0.5, (texture01.b * 0.25) + cos(0.15 * iTime), 1.0);

    vec4 blendedColor = mix(texture01, colorsPhoto, 0.3);

   // fragColor = texture01;
   fragColor = blendedColor;
}
