void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 st = uv * 10.0; // Increase the scale of the pattern
    vec2 grid = fract(st);

    vec3 colors[3];
    colors[0] = vec3(0.0, 154.0 / 255.0, 205.0 / 255.0); // Blue
    colors[1] = vec3(1.0, 215.0 / 255.0, 0.0);          // Yellow
    colors[2] = vec3(1.0, 105.0 / 255.0, 180.0 / 255.0); // Pink

    // Select color based on position
    vec3 color = colors[int(mod(floor(st.x) + floor(st.y), 3.0))];

    // Create weave effect
    if (grid.x > 0.45 && grid.x < 0.55 || grid.y > 0.45 && grid.y < 0.55) {
        color = vec3(1.0); // White for the weave effect
    }

    fragColor = vec4(color, 1.0);
}
