// Reference: Digital Rain

float text(vec2 fragCoord)
{
    vec2 uv = mod(fragCoord.xy, 16.)*.0625;
    vec2 block = fragCoord*.0625 - uv;
    uv = uv*.8+.1; // scale the letters up a bit
    uv += floor(texture(iChannel1, block/iChannelResolution[1].xy + iTime*.0002).xy * 32.); // randomize letters
    uv *= .0625; // bring back into 0-1 range
    uv.x = -uv.x; // flip letters horizontally
    return texture(iChannel0, uv).r;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2. - iResolution.xy)/iResolution.y;
    vec4 colB = vec4(0.4,0.9,0.6,1.);

    vec2 mouse = (iMouse.xy * 2. - iResolution.xy)/iResolution.y;

    // emm, some bugs on the top of the canvas.
    fragColor = mix(vec4(text(fragCoord)*(smoothstep(0.1,0.5, length(uv-mouse))-1.)*-1.),colB,uv.y)*(smoothstep(0.1,0.5, length(uv-mouse))-1.)*-1.;

}
