// CC-BY-SA 3.0

#define PI 3.1415926

vec2 normalizeCoord( in vec2 p ){
    vec2 uv = p/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;
    return uv;
}

float exp3lerp( in float p ){ return p*p*(3.-2.*p);}

float sinlerp( in float p ){return sin(p*(PI/2.));}

float square( in float p ){return p*p;}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 colorA = vec3(0.306,0.445,0.912);
    vec3 colorB = vec3(1.000,0.508,0.224);
    vec3 colorC = vec3(0.007,0.115,0.300);
    vec3 colorSun = vec3(0.985,0.478,0.544);

    vec2 uv = normalizeCoord(fragCoord);
    vec2 p = fragCoord.xy/iResolution.xy;
    vec3 pct = vec3(1.15-square(p.y));

    vec3 col = mix(colorA, colorB, pct * clamp(.0,1.,sin(iTime/PI)-.3));

    pct = vec3(exp3lerp(uv.y))*smoothstep(0.,.4, uv.y);
    col = mix(col,colorC,pct);

    pct = vec3(sinlerp(uv.y)) * smoothstep(.2,.0, uv.y) * smoothstep(1.5,0.,uv.x);
    col = mix(col,colorSun,pct * abs(sin(iTime/PI)));



    fragColor = vec4(col, 0);




}
