void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    float t = iTime * 0.5;
    vec3 O = vec3(0.0);
    for (float i = 0.0; i < 7.0; i += 1.0)
    {
        uv.x = -uv.x;
        float l = length(vec2(cos(i + t), sin(i - t)) + uv);
        O += pow(
            0.04 / abs(sin(exp(sin(l) * 0.5 - length(uv * 0.4)) * 8.0 + t * 4.0) 
            - smoothstep(0.0, 0.8, l - 0.8) * 1.5) / 
            smoothstep(0.0, 0.09, abs(l - 1.28)), 1.1
        ) * (1.0 + cos(i * 0.55 + l * 6.0 - t * 4.0 + vec3(0.0, 1.0, 2.0)));
    }

    fragColor = vec4(O, 1.0);
}
