#define PI 3.1415926

float rnd(vec2 seed) {
    return textureLod(iChannel0, seed + iTime * 0.0001,0.0).x;
    // return texture(iChannel0, seed + iTime * 0.0001).x;
    // Thanks iq for the advice!
}

mat2 r2(in float a) {
    float c = cos(a), s = sin(a);
    return mat2(c, s, -s, c);
}

vec3 getColor(int type) {
    if (type == 1) return vec3(0.34, 0.45, 1.0);
    else if (type == 2) return vec3(0.36, 0.95, 0.53);
    else if (type == 3) return vec3(1.0);
    else return vec3(0.98,0.74,0.2);
}

float circlePattern(vec2 uv, int type) {
    if (type == 1) return step(1., length(uv));
    else if (type == 2) return 1.0 - step(1., length(uv));
    else if (type == 3) return 0.0;
    else return 1.0;
}

vec3 truchetPattern(vec2 uv) {
    vec2 id = floor(uv);
    vec2 p = fract(uv);

    float randVal = rnd(id / 128.0);
    int patternType = int(randVal * 4.0);

    float angle = floor(rnd(id / 64.0) * 8.0) * (PI / 2.0);
    p = r2(angle) * (p - 0.5) + 0.5;

    float randColor = rnd(id / 256.0);
    vec3 color = getColor(int(randColor * 4.0) + 1);

    return color * vec3(circlePattern(p, patternType));
}

void mainImage(out vec4 O, in vec2 I) {
    I /= iResolution.y;
    I *= 7.0;
    I *= r2(floor(iTime/4.)* PI/2.);

    I *= r2(sin(iTime/8.)*PI/8.);    
    I -= vec2(cos(iTime/8.)*0., -iTime);

    vec3 p = clamp(truchetPattern(I),0.15,1.);
    O = vec4(p, 1.0);
}
