float sdf(vec2 uv)
{
    return min(length(uv) - 1., abs(distance(uv, vec2(2.)) - 5.) - 0.25);
}

void mainImage(out vec4 O, in vec2 I)
{
    vec2 R = iResolution.xy;
    vec2 uv = 4. * (2. * I - R) / R.y;
    vec2 uvMouse = 4. * (2. * iMouse.xy - R) / R.y;
    vec2 rayDir = normalize(uvMouse - uv);
    vec2 rayPos = uv;
    vec3 col;
    if(sdf(uv) < 0.)
    {
        col = vec3(1.);
    }
    else
    {
        for(uint i; i < 16u; i++)
        {
            rayPos += rayDir * sdf(rayPos);
        }
        col = vec3(0.5 - step(1. - dot(uvMouse - rayPos, rayDir), 1.));
    }
    O = vec4(col,1.0);
}
