float cb(vec2 p) {
    p.x*=asp;
    p=floor(S*vec2(p.x+t*.1+cos(p.y-t/2.),p.y-sin(t)*.2));
    return mod(p.x+p.y,2.);
}

vec3 mo(vec2 p) {
    return cm==0
    ?.5+.5*cos(t-length(p-.5)/.5+co)
    :.5+.5*cos(t+p.xyx+co);
}

vec4 col(vec2 p)
{
    vec4 rgb=pow(vec4(mo(p),1.),vec4(.5));
    return cb(p)==0.?smoothstep(0.2,2.,rgb):rgb;
}

