const float PI = 3.141592653;
vec2 cmul(vec2 a, vec2 b) { return vec2(a.x*b.x-a.y*b.y, a.x*b.y+a.y*b.x); }
vec2 conj(vec2 z) { return z * vec2(1., -1.); }

const float lam = 2.5;
const float alpha = -2.5;
const float beta = 0.;
const float gamma = 0.9;

vec2 doMap(vec2 z) {
    vec2 p = cmul(z, z);
    vec2 re = (lam + alpha * dot(z, z) + beta * cmul(z, z).x) * z;
    return re + conj(p) * gamma;
}

void mainImage(out vec4 col, in vec2 pos) {
    vec2 pt = 1.25 * (2. * pos - iResolution.xy) / iResolution.y;
    for (int i = 0; i < 6; i ++) {
        pt = doMap(pt);
    }
    col = vec4(floor(3. * (PI + atan(pt.y, pt.x)) / PI) / 6.);
}
