float pointInCircle(vec2 center, float r, vec2 p){
    return distance(p, center) - r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 aa = iResolution.xy / 100.0;

    //vec4 bgColor = vec4(1.0, 1.0, 1.0, 1);
    vec4 bgColor = vec4(abs(sin(iTime)), abs(cos(iTime)), abs(cos(iTime/3.)), 1.0);
    //bgColor = vec4(0.999, 0.0725, 0.5123, 1.0);
    vec3 cricle = vec3(500.0, 400.0, 200.0);
    //vec4 circleColor = vec4(1.0, 0.0, 0.0, 1.0);
    vec4 circleColor = vec4(abs(cos(iTime/2.0)), abs(sin(iTime/3.14)), abs(cos(iTime/1.5)), 1.0);
    //float d = pointInCircle(cricle.xy, cricle.z, fragCoord);
    float d = pointInCircle(vec2(0.5, 0.5)*iResolution.xy, 0.35*iResolution.y, uv*iResolution.xy);
    //d = min(d, 1.);  // fixed mix color incorrect
    //d = max(d, 0.);
    d = clamp(d, 0.0, 1.0);
    fragColor = mix(circleColor, bgColor, d);
}
