// Change red() green() and blue()


float red(float x) {
    return sin(x);
}

float green(float x)
{
    return sin(x * 1.1f);
}

float blue(float x)
{
    return sin(x * 1.2f);
}


float redBrightness = 1.0f;

float greenBrightness = 1.0f;

float blueBrightness = 1.0f;

// =======


float glbBscale = 0.05f;

float processFunc(float x, float y)
{
    return (1.0f / sqrt(pow(-(x + y), 2.0f))) * glbBscale;

}

float glbScale = 10.0f;

vec2 glbOffset = vec2(5.0f, 5.0f);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord/iResolution.xy) * glbScale - glbOffset;

    uv = vec2(uv.x, uv.y / (iResolution.x/iResolution.y));

    uv = uv * 2.0f;

    // Time varying pixel color
    vec3 col = vec3(processFunc(-red(uv.x), uv.y),
                    processFunc(-green(uv.x), uv.y),
                    processFunc(-blue(uv.x), uv.y));

    // Output to screen
    fragColor = vec4(col,1.0);
}
