#define PI acos(-1.)
#define TAU 2. * PI

// last: Dodecagon w hexagon slip
// https://www.shadertoy.com/view/4XlBDn

vec2 move(float poly, float ra, float d, float t) {
    float s = TAU / poly; 
    float T = t * poly;
    vec2 side = floor(T) * s + vec2(0, 11) + d;

    return mix(
               cos(side), 
               cos(side + s), 
               fract(T)
           ) * ra;
}

float Poly(vec2 p, float k, float ra, float T, bool fill) {
    float d;
    d = atan(p.y, p.x) - T;
    d = mod(d, TAU / k) - PI / k;
    d = cos(d) * length(p) - ra;

    return fill? d : abs(d);
}

void mainImage(out vec4 o, vec2 U){
    vec2 r = iResolution.xy,
         u = 1. * (U - r / 2.) / r.y;

    float k = 8., ds,
          t = fract(iTime * .08) * k;

    float
    t1 = (floor(t) + smoothstep(.0, .2, fract(t))) / k + 4./k,
    t2 = (floor(t) + smoothstep(.2, 1., fract(t))) / k;

    vec4 R = vec4(.24, .219, .47, .49);

    vec2 q1 = move(k, R.x, ds, t1);
    vec2 q2 = move(k, R.x, ds + PI / k, t2);

    o *= .0;

    o += max(o, smoothstep(4. / r.y, .0, length(u) - R.w)) * .125; // bg

    o.rg += max(o.g, smoothstep(4. / r.y, .0, 
            Poly(u - q1, k, R.y, ds + PI / 8., true)));
    o.gb += max(o.b, smoothstep(4. / r.y, .0, 
            Poly(u - q2, k, R.y, ds, true)));

    //if(false)
        o.r += 
              smoothstep(
                4. / r.y, .0, 
                Poly(u, k * 2., R.z, ds + PI / (2. * k), false)
              );
}
