bool sphere(vec2 u, vec2 c, float r) {
  return length(u - c) < r;
}

bool sc(vec2 uv, float t) {
  float c = cos(t);
  float s = sin(t);

  return
    // sphere
    sphere(uv, vec2(c, 0), .2) ||
    sphere(uv, vec2(0, s), .2) ||
    sphere(uv, vec2(c, s), .2) ||
    // fast
    sphere(uv, vec2(c * 2., sin(t * 3.) * .75), .2);
}

void mainImage(out vec4 O, in vec2 U) {
  vec2 uv = (U * 2. - iResolution.xy) / iResolution.y * 1.5;

  vec3 a = vec3(sc(uv, iTime));
  vec3 b = vec3(sc(uv, iTime + .01));

  O = vec4(mix(a, 1. - b, .5), 1);
}
