//вызываем функцию расчета цвета, пиксель по умолчанию черный 
//Coord — координата пикселя 
//Считаем нормализованную координату v
//iResolution - размер canvas

#define rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy-.5;


    for(float i=0.;i<5.;i++){
        uv.y=abs(sin(uv.x*8.+iTime));
        uv*=rot(iTime*i+i);
    }

    uv.x=uv.y+.5*sin(iTime);

    vec2 center = vec2(.5,.5);
    float d = distance(uv,center);

    fragColor = vec4(0, 
        sin(iTime*8. + uv.x*8.)*.5+.5, 
        sin(iTime*8. + uv.y*8.)*.5+.5, 
        1);
}
