void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    float amplitude = .08;
    float frequency = 1.;
    float phaseShift = 0.;
    float offset = .3;

    float circleRadius = amplitude * sin(frequency * (iTime + phaseShift)) + offset;

    float distToCenter = length(uv);
    float circle = smoothstep(circleRadius, circleRadius - .2, distToCenter);

    vec3 col = mix(vec3(cos(iTime + 1.), sin(iTime + 1.), .2), vec3(1.0, 1.0, 1.0), circle);
    fragColor = vec4(col,1.0);
}
