float DistLine(vec2 p, vec2 a, vec2 b)
{
    vec2 pa = p-a;
    vec2 ba = b-a;
    float t = clamp(dot(pa,ba)/dot(ba,ba), 0., 1.);
    return length(pa - ba*t);
}

float N21(vec2 p)
{
    p += vec2(9.4,1.2); // evitamos el 0
    p = fract(p*vec2(138.38,812.67));
    p += dot(p,p+23.54);
    return fract(p.x*p.y);
}

vec2 N22(vec2 p)
{
    float n = N21(p);
    return vec2(n, N21(p+n));
}

float Line(vec2 p, vec2 a, vec2 b)
{
    float d = DistLine(p, a, b);
    float s = smoothstep(.02,.01,d);
    s *= smoothstep(1.2,.6,length(a-b));
    return s;
}

vec2 GetPoint(vec2 id, vec2 offs)
{
    vec2 p = N22(id+offs)*(iTime+24.5);
    return offs - sin(p)*.4;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 col = vec3(0);
    float m = 0.;

    float t = iTime*10.;

    uv*=5.;
    vec2 c_uv = fract(uv)-.5; // como fract va de 0 a 1, restamos -.5.
    vec2 id = floor(uv);

    vec2 p[9];
    int i = 0;

    for(float y = -1.; y <= 1.; y++)
    {
        for(float x = -1.; x <= 1.; x++)
        {
            p[i++] = GetPoint(id, vec2(x,y));
        }
    }

    for(i = 0; i < 9; i++)
    {
        m += Line(c_uv, p[4], p[i]);
    }

    //Sparkle
    vec2 j = (p[4] - c_uv)*20.;
    //j += (p[3]-c_uv)*20.;
    // Descomenta para algo chulo.
    //float sparkle = clamp((1./dot(j,j*j)), 0., 1.);
    float sparkle = clamp((1./dot(j,j))-.01, 0., 1.);
    vec2 n = N22(id);
    m += sparkle*(sin(t*n.x)*.5+.5);

    // Creamos las lineas a traves del grid
    m += Line(c_uv, p[1], p[3]);
    m += Line(c_uv, p[3], p[7]);
    m += Line(c_uv, p[7], p[5]);
    m += Line(c_uv, p[5], p[1]);

    col = vec3(m);

    // Grid draw
    //if(c_uv.x > .485 || c_uv.y > .485) col = vec3(1.,0.,0.);

    col *= vec3(2.5,1.2,10.)/2.;

    fragColor = vec4(col,1.0);
}
